var modal=document.getElementById("myModal"),
body=document.getElementsByTagName("body"),
container=document.getElementById("myContainer"),
btnOpen=document.getElementById("myBtn"),
btnClose=document.getElementById("closeModal");

// Blurs background (main container) and removed the hidden property from modal
// Modal pops up, background is bluured
btnOpen.onclick=function(){
    modal.className="Modal is-visuallyHidden",
    setTimeout(function(){
        container.className="MainContainer is-blurred",
        modal.className="Modal"} // modal is no longer hidden, as is-hidden is removed from class name
    ,100)},


// Reverts the changes.
btnClose.onclick=function(){
    modal.className="Modal is-hidden is-visuallyHidden", // hides the modal again
    //body.className="",
    container.className="MainContainer" // no longer blurred
    //container.parentElement.className=""
    }

